// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import cartIcon from '../assets/cart-icon.png'; // Ajusta la ruta si es necesario
import './Navbar.css'; // Asegúrate de que la ruta sea correcta
import { useCart } from '../context/CartContext';

const Navbar = () => {
  const [isCartVisible, setCartVisible] = useState(false);
  const { cartItems, removeFromCart } = useCart(); // Asegúrate de que el contexto esté disponible

  const toggleCart = () => {
    setCartVisible(!isCartVisible);
  };

  const handleCheckout = () => {
    setCartVisible(false);
    window.location.href = '/checkout'; // Redirige al checkout
  };

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">Logo</Link>
      <ul className="navbar-list">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link to="/profile">Profile</Link></li>
        <li><Link to="/login">Login</Link></li>
        <li><Link to="/register">Register</Link></li>
      </ul>
      <div className="navbar-cart">
        <button className="cart-button" onClick={toggleCart}>
          <img src={cartIcon} alt="Cart" className="cart-icon" />
          <span className="cart-count">{cartItems.length}</span>
        </button>
        <div className={`cart-dropdown ${isCartVisible ? 'show' : ''}`}>
          {cartItems.length === 0 ? (
            <p>No items in cart</p>
          ) : (
            <ul className="cart-items">
              {cartItems.map(item => (
                <li className="cart-item" key={item._id}>
                  <img src={item.imageUrl} alt={item.name} className="cart-item-image" />
                  <div className="cart-item-details">
                    <p>{item.name}</p>
                    <p>${item.price.toFixed(2)}</p>
                    <button className="remove-button" onClick={() => removeFromCart(item._id)}>Remove</button>
                  </div>
                </li>
              ))}
            </ul>
          )}
          <button className="checkout-button" onClick={handleCheckout}>Iniciar Pago</button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
