// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaShoppingCart } from 'react-icons/fa'; // Importa el ícono de carrito
import { useCart } from '../context/CartContext'; // Ajusta la ruta según tu estructura
import './Navbar.css'; // Asegúrate de que el archivo CSS esté importado

const Navbar = () => {
  const [showCart, setShowCart] = useState(false);
  const { cart } = useCart(); // Obtén el estado del carrito

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  return (
    <nav className="navbar">
      <ul className="navbar-list">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link to="/profile">Profile</Link></li>
        <li><Link to="/checkout">Checkout</Link></li>
        <li><Link to="/login">Login</Link></li>
        <li><Link to="/register">Register</Link></li>
        <li className="navbar-cart">
          <button className="cart-button" onClick={toggleCart}>
            <FaShoppingCart size={24} />
            {cart.length > 0 && (
              <span className="cart-count">{cart.length}</span>
            )}
          </button>
          {showCart && (
            <div className="cart-dropdown">
              <h3>Your Cart</h3>
              <ul className="cart-items">
                {cart.map(item => (
                  <li key={item._id} className="cart-item">
                    <img src={item.imageUrl} alt={item.name} className="cart-item-image" />
                    <div className="cart-item-details">
                      <p className="cart-item-name">{item.name}</p>
                      <p className="cart-item-price">${item.price.toFixed(2)}</p>
                    </div>
                  </li>
                ))}
              </ul>
              <Link to="/checkout" className="checkout-button">Iniciar Pago</Link>
            </div>
          )}
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
