// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import cartIcon from '../assets/cart-icon.png'; // Ajusta la ruta si es necesario
import './Navbar.css'; // Asegúrate de que la ruta sea correcta
import { useCart } from '../context/CartContext';

const Navbar = () => {
  const [isCartVisible, setCartVisible] = useState(false);
  const { cartItems } = useCart(); // Asegúrate de que el contexto esté disponible

  const toggleCart = () => {
    setCartVisible(!isCartVisible);
  };

  const handleCheckout = () => {
    setCartVisible(false);
    window.location.href = '/checkout'; // Redirige al checkout
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Logo</Link>
      </div>
      <div className="navbar-menu">
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/products">Products</Link></li>
          <li><Link to="/profile">Profile</Link></li>
          <li><Link to="/login">Login</Link></li>
          <li><Link to="/register">Register</Link></li>
        </ul>
      </div>
      <div className="cart-icon" onClick={toggleCart}>
        <img src={cartIcon} alt="Cart" />
        <div className={`cart-dropdown ${isCartVisible ? 'show' : ''}`}>
          {cartItems.length === 0 ? (
            <p>No items in cart</p>
          ) : (
            cartItems.map(item => (
              <div className="cart-dropdown-item" key={item._id}>
                <p>{item.name}</p>
                <button onClick={() => removeFromCart(item._id)}>Remove</button>
              </div>
            ))
          )}
          <button className="cart-dropdown-button" onClick={handleCheckout}>Iniciar Pago</button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
