import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useCart } from '../context/CartContext'; // Asegúrate de ajustar la ruta del archivo
import './ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const { addToCart } = useCart(); // Usa el contexto del carrito

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    console.log('Adding to cart:', product); // Agrega este log para depuración
    addToCart(product);
  };

  return (
    <div className="product-list-container">
      <h2 className="product-list-title">Product List</h2>
      <div className="product-grid">
        {products.map(product => (
          <div className="product-card" key={product._id}>
            <img src={product.imageUrl} alt={product.name} className="product-image" />
            <div className="product-info">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-price">${product.price.toFixed(2)}</p>
              <p className="product-description">{product.description}</p>
              <button className="product-button" onClick={() => handleAddToCart(product)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
