import React, { useState } from 'react';
import axios from '../axios'; // Asegúrate de que la ruta de axios sea correcta
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isEmailValid, setIsEmailValid] = useState(true);
  const [isPasswordValid, setIsPasswordValid] = useState(true);
  const navigate = useNavigate();

  const validateEmail = (email) => {
    // Validación básica de email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    // La contraseña debe tener al menos 6 caracteres
    return password.length >= 6;
  };

  const handleEmailChange = (e) => {
    const email = e.target.value;
    setEmail(email);
    setIsEmailValid(validateEmail(email));
  };

  const handlePasswordChange = (e) => {
    const password = e.target.value;
    setPassword(password);
    setIsPasswordValid(validatePassword(password));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email || !password || !isEmailValid || !isPasswordValid) {
      setError('Please fill in all fields correctly.');
      return;
    }
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('/auth/register', { email, password });
      if (response.status === 201) {
        navigate('/profile'); // Redirige a la página de perfil si el registro es exitoso
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'Registration failed. Please try again.';
      setError(errorMessage);
      console.error('Error registering:', error.response ? error.response.data : error.message);
    } finally {
      setLoading(false); // Detiene el estado de carga
    }
  };

  return (
    <div className="register-container">
      <div className="register-form">
        <h1>Register</h1>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={handleEmailChange}
              required
              className={!isEmailValid ? 'input-error' : ''}
              aria-invalid={!isEmailValid}
              aria-describedby="email-error"
            />
            {!isEmailValid && <p id="email-error" className="error-text">Please enter a valid email address.</p>}
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={handlePasswordChange}
              required
              minLength="6"
              placeholder="Minimum 6 characters"
              className={!isPasswordValid ? 'input-error' : ''}
              aria-invalid={!isPasswordValid}
              aria-describedby="password-error"
            />
            {!isPasswordValid && <p id="password-error" className="error-text">Password must be at least 6 characters long.</p>}
          </div>
          <button type="submit" className="submit-button" disabled={loading}>
            {loading ? 'Registering...' : 'Register'}
          </button>
        </form>
        <p className="login-link">Already have an account? <a href="/login">Login here</a></p>
      </div>
    </div>
  );
};

export default Register;
